import React, {useRef} from 'react'

const allStrat=["c all","l all","reverse"]

export default function Strategy(){
    const Ref = useRef();
    const handleChange=()=>{
        console.log(Ref.current)
    }
    return <div>
        <SelectWithOption test={Ref} handleChange={handleChange}></SelectWithOption>
        <button>Play with this Strategy</button>
    </div>

}

function SelectWithOption({test,handleChange}){

    return <select ref={test} onChange={handleChange}>
        {
            allStrat.map((value)=>{
                return <option value = {value} key={value}>{value}</option>
            })
        }
    </select>
}
