import React, {useRef} from 'react'

const allStrat=["c all","l all","reverse"]

export default function Strategy(){
    const selectRef = useRef();
    const handleClick = ()=>{
        console.log(selectRef.current.value)
    }
    return <div>
        <SelectWithOption ref={selectRef}></SelectWithOption>
        <button onClick={handleClick}>Play with this Strategy</button>
    </div>

}

function SelectWithOption({ref,handleChange}){

    return <select ref={ref} onChange={handleChange}>
        {
            allStrat.map((value)=>{
                return <option value={value} key={value}>{value}</option>
            })
        }
    </select>
}
